import React, { useRef, useEffect } from 'react';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
// import img from '../../images/pages/home/floss3d.png';
// import img from '../../images/pages/home/floss_home.png';
import img from '../../images/pages/home/video.webm';
import { useContext } from 'react';
import * as API from '../../data/dataAPI';
import { LanguageContext } from '../../data/language';
import { Link} from 'react-router-dom';

// Register ScrollTrigger plugin
gsap.registerPlugin(ScrollTrigger);
const FlossHome = () => {
  const boxRef = useRef(null);
  const flossRef = useRef(null);
  const language = useContext(LanguageContext);
  const flossLiterals = API.getComponents('flossHome', language.lang)
  useEffect(() => {
    initGsap();
    const handleResize = () => {
      initGsap();
    };
    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);
  const initGsap = () => {
    const widths = [window.innerWidth];
    if (window.screen?.width) {
      widths.push(window.screen?.width);
    }
    const width = Math.min(...widths);
    let flossWidth;
    let containerWidth;
    if (width >= 1024) {
      flossWidth = '475px';
      containerWidth = 'calc(100% - 112px)';
    } 
    else if (width >= 768) {
      flossWidth = '400px';
      containerWidth = 'calc(100% - 32px)';
    } 
    else {
      flossWidth = '350px';
      containerWidth = 'calc(100% - 32px)';
    }
    let finalWidth = width >= 1920 ? '1920px' : containerWidth;
    if (boxRef.current) {
      boxRef.current.style.width = '100%';
      gsap.to(boxRef.current, {
        width: finalWidth,
        borderRadius: '30px',
        scrollTrigger: {
          trigger: boxRef.current,
          start: 'top top',
          end: 'center top',
          scrub: true
        },
      });
    }
    
    if (flossRef.current) {
      gsap.to(flossRef.current, {
        width: flossWidth,
        bottom: '0',
        scrollTrigger: {
          trigger: boxRef.current,
          start: 'top top',
          end: 'center top',
          scrub: true
        },
      });
    }
  };

  return (
    <div className="floss-home-container">
      <div className="center-container" ref={boxRef}>
        <div className="container">
          <div className="title-container observeElement">
            <div className="title tr">{flossLiterals.title}</div>
          </div>
          <div className="button-container observeElement">
            <Link to='/Visit-us' className="button black small">{flossLiterals.button}</Link>
          </div>
        </div>
        <div className="floss" ref={flossRef}>
        <video autoPlay playsInline muted loop>
            <source src={img} type="video/webm" />
            Your browser does not support the video tag.
          </video>
        </div>
      </div>
    </div>
  );
};

export default FlossHome;
