import { useState, useContext } from 'react';
import { ReactComponent as Icon1 } from '../images/svg/location_on.svg'
import { ReactComponent as Icon2 } from '../images/svg/call.svg'
import { ReactComponent as Icon3 } from '../images/svg/appointment.svg'
import { ReactComponent as Icon4 } from '../images/svg/language.svg'
import { LanguageContext } from '../data/language';
import { Link } from 'react-router-dom';
function Menu(props) {
  const [lang, setLang] = useState(useContext(LanguageContext).lang);
  const clickLanguageSelector = () => {
    if (lang === 'ger')
        setLang('en')
    else
        setLang('ger')
    const allTranslations = document.querySelectorAll('.tr');
    const menuButton = document.querySelector('.change-language');
    allTranslations.forEach(e=> e.classList.add('brush'))
    menuButton.classList.add('brush')
    setTimeout(function() {
        props.changeLanguage();
      }, 500);
    setTimeout(function() {
        allTranslations.forEach(e=> e.classList.remove('brush'))
        menuButton.classList.remove('brush')
      }, 1000);
    
}
  return (
    <div className='menu-container'>
        <ul className='icons-container'>
            <li>
                <a href=''>
                    <Icon1 />
                    <span className='label'>{props.directions}</span>
                </a>
            </li>
            <li>
                <a href=''>
                    <Icon2 />
                    <span className='label'>{props.callUs}</span>
                </a> 
            </li>
            <li>
                <Link to='/Visit-us'>
                    <Icon3 />
                    <span className='label'>{props.appointment}</span>
                </Link>
            </li>
            <li>
                <span class='hoverable change-language' onClick={() => clickLanguageSelector()}>
                    <Icon4 />
                    <span className='label'>{lang==='en' ? 'german' : 'englisch' }</span>
                </span>
            </li>
        </ul>
        <div className='menu-button-container'>
            <div className='menu-button' onClick={()=>props.setOpenMenu(!props.openMenu)}>
                <span className='label'>Menu</span>
                <div className={`menu-icon ${props.openMenu===true? 'opened':'closed'}`}>
                    <span className='first'></span>
                    <span className='second'></span>
                    <span className='third'></span>
                    <span className='fourth'></span>
                </div>
            </div>
        </div>
        
    </div>
  )
}
export default Menu;
